import React, { useState } from 'react';
import { ShoppingCart, Plus, Minus, Trash2, Search, Store, Shield, Star, User, LogIn } from 'lucide-react';

const RobuxMarket = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(false);
  const [showSellerForm, setShowSellerForm] = useState(false);
  const [products, setProducts] = useState([
    { id: 1, name: '800 Robux', price: 95000, image: 'ðŸ’Ž', category: 'Robux', seller: 'TrustedSeller99', rating: 4.9, sales: 1523, stock: 50 },
    { id: 2, name: '1700 Robux', price: 185000, image: 'ðŸ’Ž', category: 'Robux', seller: 'GameStore', rating: 4.8, sales: 892, stock: 30 },
    { id: 3, name: 'Valorant 475 VP', price: 45000, image: 'ðŸŽ®', category: 'Valorant', seller: 'ValorantPro', rating: 4.7, sales: 445, stock: 100 },
    { id: 4, name: 'ML 1000 Diamond', price: 250000, image: 'ðŸ’ ', category: 'Mobile Legends', seller: 'MLStore', rating: 4.9, sales: 2341, stock: 80 },
    { id: 5, name: 'PUBG 3850 UC', price: 950000, image: 'ðŸ”«', category: 'PUBG', seller: 'PUBGMaster', rating: 4.8, sales: 567, stock: 40 },
    { id: 6, name: 'Steam Wallet 120K', price: 130000, image: 'ðŸŽ¯', category: 'Steam', seller: 'SteamDeals', rating: 4.9, sales: 3421, stock: 200 }
  ]);

  const [cart, setCart] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('Semua');
  const [activeTab, setActiveTab] = useState('browse');
  const [newProduct, setNewProduct] = useState({ name: '', price: '', category: 'Robux', stock: '', image: 'ðŸ’Ž' });

  const categories = ['Semua', ...new Set(products.map(p => p.category))];
  const gameIcons = { 'Robux': 'ðŸ’Ž', 'Valorant': 'ðŸŽ®', 'Mobile Legends': 'ðŸ’ ', 'PUBG': 'ðŸ”«', 'Steam': 'ðŸŽ¯', 'Free Fire': 'ðŸ”¥' };

  const handleLogin = () => {
    setUser({ name: 'User123', balance: 500000 });
    setShowAuth(false);
  };

  const handleAddProduct = () => {
    if (!user) {
      alert('Silakan login terlebih dahulu!');
      return;
    }
    const product = {
      id: Date.now(),
      name: newProduct.name,
      price: parseInt(newProduct.price),
      image: gameIcons[newProduct.category],
      category: newProduct.category,
      seller: user.name,
      rating: 5.0,
      sales: 0,
      stock: parseInt(newProduct.stock)
    };
    setProducts([...products, product]);
    setNewProduct({ name: '', price: '', category: 'Robux', stock: '', image: 'ðŸ’Ž' });
    setShowSellerForm(false);
    alert('Produk berhasil ditambahkan!');
  };

  const filteredProducts = products.filter(p => 
    p.name.toLowerCase().includes(search.toLowerCase()) &&
    (selectedCategory === 'Semua' || p.category === selectedCategory)
  );

  const addToCart = (product) => {
    if (!user) {
      alert('Silakan login terlebih dahulu!');
      setShowAuth(true);
      return;
    }
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      if (existingItem.quantity < product.stock) {
        setCart(cart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item));
      }
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const updateQuantity = (id, change) => {
    const item = cart.find(item => item.id === id);
    const product = products.find(p => p.id === id);
    const newQuantity = item.quantity + change;
    if (newQuantity > 0 && newQuantity <= product.stock) {
      setCart(cart.map(item => item.id === id ? { ...item, quantity: newQuantity } : item));
    }
  };

  const removeFromCart = (id) => setCart(cart.filter(item => item.id !== id));
  const totalPrice = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  const formatPrice = (price) => new Intl.NumberFormat('id-ID', { style: 'currency', currency: 'IDR', minimumFractionDigits: 0 }).format(price);

  const handleCheckout = () => {
    if (cart.length === 0) return alert('Keranjang masih kosong!');
    if (user.balance < totalPrice) return alert('Saldo tidak cukup!');
    alert(`Pembayaran berhasil! Total: ${formatPrice(totalPrice)}`);
    setCart([]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50">
      <header className="bg-white shadow-lg sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <span className="text-3xl">ðŸŽ®</span>
              <h1 className="text-2xl sm:text-3xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">RobuxMarket</h1>
            </div>
            <div className="flex items-center gap-2 sm:gap-4">
              {user ? (
                <>
                  <div className="hidden sm:flex items-center gap-2 bg-green-50 px-3 py-2 rounded-lg">
                    <span className="text-xs font-bold text-green-600">{formatPrice(user.balance)}</span>
                  </div>
                  <div className="flex items-center gap-2 bg-purple-50 px-3 py-2 rounded-lg">
                    <User className="w-4 h-4 text-purple-600" />
                    <span className="text-sm font-semibold text-purple-600">{user.name}</span>
                  </div>
                </>
              ) : (
                <button onClick={() => setShowAuth(true)} className="flex items-center gap-2 bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 text-sm font-semibold">
                  <LogIn className="w-4 h-4" />Login
                </button>
              )}
              <div className="relative">
                <ShoppingCart className="w-7 h-7 text-purple-600" />
                {totalItems > 0 && <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs font-bold">{totalItems}</span>}
              </div>
            </div>
          </div>
        </div>
      </header>

      {showAuth && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4 text-center">Login ke Akun</h2>
            <div className="space-y-4">
              <input type="text" placeholder="Username" className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
              <input type="password" placeholder="Password" className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
              <button onClick={handleLogin} className="w-full bg-purple-600 text-white py-3 rounded-lg hover:bg-purple-700 font-semibold">Login</button>
            </div>
            <button onClick={() => setShowAuth(false)} className="mt-4 w-full text-gray-600">Tutup</button>
          </div>
        </div>
      )}

      <div className="max-w-7xl mx-auto px-4 py-4">
        <div className="flex gap-2 bg-white rounded-lg p-2 shadow-md">
          <button onClick={() => setActiveTab('browse')} className={`flex-1 py-3 rounded-lg font-semibold ${activeTab === 'browse' ? 'bg-purple-600 text-white' : 'text-gray-600'}`}>
            ðŸ›ï¸ Belanja
          </button>
          <button onClick={() => { if (!user) { alert('Login dulu!'); setShowAuth(true); return; } setActiveTab('sell'); }} className={`flex-1 py-3 rounded-lg font-semibold ${activeTab === 'sell' ? 'bg-purple-600 text-white' : 'text-gray-600'}`}>
            <Store className="w-5 h-5 inline mr-2" />Jual
          </button>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-6">
        {activeTab === 'browse' ? (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2">
              <div className="bg-white rounded-lg shadow-md p-4 mb-6">
                <div className="relative mb-4">
                  <Search className="absolute left-3 top-3 text-gray-400 w-5 h-5" />
                  <input type="text" placeholder="Cari Robux, Diamond ML..." value={search} onChange={(e) => setSearch(e.target.value)} className="w-full pl-10 pr-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
                </div>
                <div className="flex gap-2 flex-wrap">
                  {categories.map(cat => (
                    <button key={cat} onClick={() => setSelectedCategory(cat)} className={`px-4 py-2 rounded-full text-sm font-medium ${selectedCategory === cat ? 'bg-gradient-to-r from-purple-600 to-pink-600 text-white' : 'bg-gray-100 text-gray-700'}`}>
                      {cat}
                    </button>
                  ))}
                </div>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                {filteredProducts.map(product => (
                  <div key={product.id} className="bg-white rounded-xl shadow-md p-6 hover:shadow-xl transition-all">
                    <div className="text-6xl text-center mb-4">{product.image}</div>
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-xs font-semibold text-purple-600 bg-purple-100 px-3 py-1 rounded-full">{product.category}</span>
                      <div className="flex items-center gap-1">
                        <Star className="w-4 h-4 text-yellow-400 fill-yellow-400" />
                        <span className="text-sm font-semibold">{product.rating}</span>
                      </div>
                    </div>
                    <h3 className="text-lg font-bold mb-2">{product.name}</h3>
                    <div className="flex items-center gap-2 mb-3">
                      <User className="w-4 h-4 text-gray-400" />
                      <span className="text-sm text-gray-600">{product.seller}</span>
