                <div className="space-y-6">
                  <div>
                    <label className="block text-sm font-semibold mb-2">Nama Produk</label>
                    <input type="text" value={newProduct.name} onChange={(e) => setNewProduct({...newProduct, name: e.target.value})} placeholder="800 Robux" className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
                  </div>
                  <div>
                    <label className="block text-sm font-semibold mb-2">Kategori</label>
                    <select value={newProduct.category} onChange={(e) => setNewProduct({...newProduct, category: e.target.value, image: gameIcons[e.target.value]})} className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500">
                      {Object.keys(gameIcons).map(cat => <option key={cat} value={cat}>{gameIcons[cat]} {cat}</option>)}
                    </select>
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-semibold mb-2">Harga (Rp)</label>
                      <input type="number" value={newProduct.price} onChange={(e) => setNewProduct({...newProduct, price: e.target.value})} placeholder="50000" className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
                    </div>
                    <div>
                      <label className="block text-sm font-semibold mb-2">Stok</label>
                      <input type="number" value={newProduct.stock} onChange={(e) => setNewProduct({...newProduct, stock: e.target.value})} placeholder="10" className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-500" />
                    </div>
                  </div>
                  <div className="flex gap-4">
                    <button onClick={handleAddProduct} className="flex-1 bg-gradient-to-r from-purple-600 to-pink-600 text-white py-3 rounded-lg hover:from-purple-700 hover:to-pink-700 font-semibold">âœ… Tambah Produk</button>
                    <button onClick={() => setShowSellerForm(false)} className="px-6 py-3 border-2 rounded-lg hover:bg-gray-50 font-semibold">Batal</button>
                  </div>
                </div>
              )}
              {user && products.filter(p => p.seller === user.name).length > 0 && (
                <div className="mt-12">
                  <h3 className="text-2xl font-bold mb-4">Produk Saya</h3>
                  <div className="space-y-4">
                    {products.filter(p => p.seller === user.name).map(product => (
                      <div key={product.id} className="flex items-center gap-4 p-4 bg-gradient-to-r from-purple-50 to-pink-50 rounded-lg">
                        <div className="text-4xl">{product.image}</div>
                        <div className="flex-1">
                          <h4 className="font-bold">{product.name}</h4>
                          <div className="flex gap-4 text-sm text-gray-600 mt-1">
                            <span>ðŸ’° {formatPrice(product.price)}</span>
                            <span>ðŸ“¦ Stok: {product.stock}</span>
                            <span>ðŸ“Š {product.sales} terjual</span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RobuxMarket;
